import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup, useMap } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import axios from 'axios';
import L from 'leaflet';
import './HospitalLocator.css';

// Custom marker icons
const hospitalIcon = new L.Icon({
    iconUrl: 'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-red.png',
    iconSize: [25, 41],
    iconAnchor: [12, 41],
    popupAnchor: [1, -34],
    shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
    shadowSize: [41, 41]
});

const userIcon = new L.Icon({
    iconUrl: 'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-blue.png',
    iconSize: [25, 41],
    iconAnchor: [12, 41],
    popupAnchor: [1, -34],
    shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
    shadowSize: [41, 41]
});

function ChangeView({ center, zoom }) {
    const map = useMap();
    map.setView(center, zoom);
    return null;
}

const HospitalLocator = () => {
    const [hospitals, setHospitals] = useState([]);
    const [currentLocation, setCurrentLocation] = useState([51.505, -0.09]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [selectedHospital, setSelectedHospital] = useState(null);

    useEffect(() => {
        navigator.geolocation.getCurrentPosition(
            position => {
                const { latitude, longitude } = position.coords;
                setCurrentLocation([latitude, longitude]);
                fetchNearbyHospitals(latitude, longitude);
            },
            error => {
                console.error('Error getting location:', error);
                setError('Could not get your location. Please enable location services.');
                setLoading(false);
            },
            { enableHighAccuracy: true }
        );
    }, []);

    const fetchNearbyHospitals = async (lat, lng) => {
        try {
            setLoading(true);
            const response = await axios.get(
                `${process.env.REACT_APP_API_URL}/api/hospitals/nearby`,
                { params: { latitude: lat, longitude: lng } }
            );

            if (response.data && Array.isArray(response.data)) {
                setHospitals(response.data);
            } else {
                setError('Invalid response format from server');
                setHospitals([]);
            }
        } catch (err) {
            console.error('Error fetching hospitals:', err);
            setError('Could not fetch nearby hospitals. Please try again later.');
        } finally {
            setLoading(false);
        }
    };

    if (loading) {
        return (
            <div className="loading-container">
                <div className="loading-spinner"></div>
                <h2>Finding Nearby Hospitals</h2>
                <p>Please wait while we locate hospitals in your area...</p>
            </div>
        );
    }

    if (error) {
        return (
            <div className="error-container">
                <div className="error-icon">‚ö†Ô∏è</div>
                <h2>Error</h2>
                <p>{error}</p>
                <button onClick={() => window.location.reload()} className="retry-button">
                    Try Again
                </button>
            </div>
        );
    }

    return (
        <div className="hospital-locator-container">
            <div className="sidebar">
                <div className="header">
                    <h1>Nearby Hospitals</h1>
                    <p>Found {hospitals.length} hospitals in your area</p>
                </div>
                <div className="hospital-list">
                    {hospitals.map(hospital => (
                        <div 
                            key={hospital.id}
                            className={`hospital-card ${selectedHospital?.id === hospital.id ? 'selected' : ''}`}
                            onClick={() => setSelectedHospital(hospital)}
                        >
                            <h3>{hospital.name}</h3>
                            <p className="address">{hospital.address}</p>
                            <div className="hospital-details">
                                <div className="detail-item">
                                    <span className="icon">üìû</span>
                                    <span>{hospital.phone || 'N/A'}</span>
                                </div>
                                <div className="detail-item">
                                    <span className="icon">üöë</span>
                                    <span>Emergency: {hospital.emergency}</span>
                                </div>
                                <div className="detail-item">
                                    <span className="icon">‚ôø</span>
                                    <span>Wheelchair: {hospital.wheelchair}</span>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
            <div className="map-container">
                <MapContainer 
                    center={currentLocation} 
                    zoom={13} 
                    style={{ height: '100%', width: '100%' }}
                >
                    <ChangeView center={currentLocation} zoom={13} />
                    <TileLayer
                        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                    />
                    
                    <Marker position={currentLocation} icon={userIcon}>
                        <Popup>
                            <div className="current-location-popup">
                                <h3>Your Location</h3>
                                <p>You are here</p>
                            </div>
                        </Popup>
                    </Marker>

                    {hospitals.map(hospital => (
                        <Marker
                            key={hospital.id}
                            position={[hospital.lat, hospital.lon]}
                            icon={hospitalIcon}
                        >
                            <Popup>
                                <div className="hospital-popup">
                                    <h3>{hospital.name}</h3>
                                    <p><strong>Address:</strong> {hospital.address}</p>
                                    <p><strong>Phone:</strong> {hospital.phone}</p>
                                    {hospital.website !== 'N/A' && (
                                        <p>
                                            <strong>Website:</strong>{' '}
                                            <a href={hospital.website} target="_blank" rel="noopener noreferrer">
                                                Visit website
                                            </a>
                                        </p>
                                    )}
                                    <div className="hospital-status">
                                        <span className={`status-badge ${hospital.emergency === 'Yes' ? 'emergency' : ''}`}>
                                            {hospital.emergency === 'Yes' ? 'üöë Emergency Services' : '‚öïÔ∏è Non-Emergency'}
                                        </span>
                                        <span className={`status-badge ${hospital.wheelchair !== 'Unknown' ? 'accessible' : ''}`}>
                                            ‚ôø {hospital.wheelchair}
                                        </span>
                                    </div>
                                </div>
                            </Popup>
                        </Marker>
                    ))}
                </MapContainer>
            </div>
        </div>
    );
};

export default HospitalLocator;
