import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup, useMap } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import axios from 'axios';
import L from 'leaflet';

// Fix for default marker icon
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
    iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
    iconUrl: require('leaflet/dist/images/marker-icon.png'),
    shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

// Custom marker icons
const hospitalIcon = new L.Icon({
    iconUrl: 'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-red.png',
    iconSize: [25, 41],
    iconAnchor: [12, 41],
    popupAnchor: [1, -34],
});

// Component to update map view when location changes
function ChangeView({ center, zoom }) {
    const map = useMap();
    map.setView(center, zoom);
    return null;
}

const HospitalLocator = () => {
    const [hospitals, setHospitals] = useState([]);
    const [currentLocation, setCurrentLocation] = useState([51.505, -0.09]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        navigator.geolocation.getCurrentPosition(
            position => {
                const { latitude, longitude } = position.coords;
                setCurrentLocation([latitude, longitude]);
                fetchNearbyHospitals(latitude, longitude);
            },
            error => {
                console.error('Error getting location:', error);
                setError('Could not get your location. Please enable location services.');
                setLoading(false);
            },
            { enableHighAccuracy: true }
        );
    }, []);

    const fetchNearbyHospitals = async (lat, lng) => {
        try {
            setLoading(true);
            const response = await axios.get(
                `${process.env.REACT_APP_API_URL}/api/hospitals/nearby`,
                {
                    params: { latitude: lat, longitude: lng }
                }
            );

            if (response.data && Array.isArray(response.data)) {
                setHospitals(response.data);
            } else {
                setError('Invalid response format from server');
                setHospitals([]);
            }
        } catch (err) {
            console.error('Error fetching hospitals:', err);
            setError('Could not fetch nearby hospitals. Please try again later.');
        } finally {
            setLoading(false);
        }
    };

    if (loading) {
        return (
            <div style={{ textAlign: 'center', padding: '20px' }}>
                <h2>Loading nearby hospitals...</h2>
                <p>Please wait while we find hospitals in your area.</p>
            </div>
        );
    }

    if (error) {
        return (
            <div style={{ textAlign: 'center', padding: '20px', color: 'red' }}>
                <h2>Error</h2>
                <p>{error}</p>
            </div>
        );
    }

    return (
        <div>
            <div style={{ padding: '10px' }}>
                <h2>Nearby Hospitals</h2>
                <p>Found {hospitals.length} hospitals in your area</p>
            </div>
            <div style={{ height: '500px', width: '100%' }}>
                <MapContainer 
                    center={currentLocation} 
                    zoom={13} 
                    style={{ height: '100%', width: '100%' }}
                >
                    <ChangeView center={currentLocation} zoom={13} />
                    <TileLayer
                        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                    />
                    
                    {/* Current location marker */}
                    <Marker position={currentLocation}>
                        <Popup>
                            <b>Your Location</b>
                        </Popup>
                    </Marker>

                    {/* Hospital markers */}
                    {hospitals.map(hospital => (
                        <Marker
                            key={hospital.id}
                            position={[hospital.lat, hospital.lon]}
                            icon={hospitalIcon}
                        >
                            <Popup>
                                <div style={{ minWidth: '200px' }}>
                                    <h3>{hospital.name}</h3>
                                    <p><strong>Address:</strong> {hospital.address}</p>
                                    <p><strong>Phone:</strong> {hospital.phone}</p>
                                    {hospital.website !== 'N/A' && (
                                        <p><strong>Website:</strong> <a href={hospital.website} target="_blank" rel="noopener noreferrer">Visit website</a></p>
                                    )}
                                    <p><strong>Emergency:</strong> {hospital.emergency}</p>
                                    <p><strong>Wheelchair Access:</strong> {hospital.wheelchair}</p>
                                    <p><strong>Distance:</strong> {hospital.distance}</p>
                                </div>
                            </Popup>
                        </Marker>
                    ))}
                </MapContainer>
            </div>
        </div>
    );
};

export default HospitalLocator;
