import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import axios from 'axios';
import L from 'leaflet';

// Fix for default marker icon
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
    iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
    iconUrl: require('leaflet/dist/images/marker-icon.png'),
    shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

const HospitalLocator = () => {
    const [hospitals, setHospitals] = useState([]);
    const [currentLocation, setCurrentLocation] = useState([51.505, -0.09]); // Default location
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        navigator.geolocation.getCurrentPosition(
            position => {
                const { latitude, longitude } = position.coords;
                setCurrentLocation([latitude, longitude]);
                fetchNearbyHospitals(latitude, longitude);
            },
            error => {
                console.error('Error getting location:', error);
                setError('Could not get your location');
                setLoading(false);
            }
        );
    }, []);

    const fetchNearbyHospitals = async (lat, lng) => {
        try {
            const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/hospitals/nearby?latitude=${lat}&longitude=${lng}`);
            if (response.data && Array.isArray(response.data)) {
                setHospitals(response.data);
            } else {
                setHospitals([]);
            }
            setLoading(false);
        } catch (err) {
            console.error('Error fetching hospitals:', err);
            setError('Could not fetch nearby hospitals');
            setLoading(false);
        }
    };

    if (loading) {
        return <div>Loading map...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div style={{ height: '500px', width: '100%' }}>
            <MapContainer 
                center={currentLocation} 
                zoom={13} 
                style={{ height: '100%', width: '100%' }}
            >
                <TileLayer
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                />
                {/* Current location marker */}
                <Marker position={currentLocation}>
                    <Popup>You are here</Popup>
                </Marker>
                
                {/* Hospital markers */}
                {hospitals.map(hospital => (
                    hospital.lat && hospital.lon ? (
                        <Marker
                            key={hospital.id || Math.random()}
                            position={[hospital.lat, hospital.lon]}
                        >
                            <Popup>
                                <div>
                                    <h3>{hospital.name || 'Hospital'}</h3>
                                    <p>{hospital.address || 'Address not available'}</p>
                                </div>
                            </Popup>
                        </Marker>
                    ) : null
                ))}
            </MapContainer>
        </div>
    );
};

export default HospitalLocator;
